// src/pages/StudentsPage.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import StudentTable from '../components/StudentTable';
import StudentForm from '../components/StudentForm'; // Import the new form

function StudentsPage() {
  // The state for the student list now lives here
  const [students, setStudents] = useState([]);

  // Fetch students when the component loads
  useEffect(() => {
    const fetchStudents = async () => {
      try {
        const response = await axios.get('http://127.0.0.1:8000/api/students/');
        setStudents(response.data);
      } catch (error) {
        console.error("There was an error fetching the students!", error);
      }
    };
    fetchStudents();
  }, []);

  // This function will be called by the form after a student is created
  const handleStudentCreated = (newStudent) => {
    // Add the new student to the existing list in the state
    setStudents([...students, newStudent]);
  };
  const handleDeleteStudent = async (rollNumber) => {
    // Ask for confirmation before deleting
    if (window.confirm('Are you sure you want to delete this student?')) {
      try {
        // Send a DELETE request to the backend API
        await axios.delete(`http://127.0.0.1:8000/api/students/${rollNumber}/`);

        // If successful, update the state to remove the deleted student
        // The filter method creates a new array with all students EXCEPT the one we deleted.
        setStudents(students.filter(student => student.roll_number !== rollNumber));

        alert('Student deleted successfully!');
      } catch (error) {
        console.error("There was an error deleting the student!", error);
        alert('Error deleting student.');
      }
    }
  };


  return (
    <div>
      <h1>Student Management</h1>
      <p>Here you can view and manage student records.</p>
      <hr />
      {/* Pass the function down to the form component as a prop */}
      <StudentForm onStudentCreated={handleStudentCreated} />

      {/* Pass the student list down to the table component as a prop */}
      <StudentTable students={students} onDeleteStudent={handleDeleteStudent} />
    </div>
  );
}

export default StudentsPage;
