// src/components/Sidebar.js
import React from 'react';
import { Link } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';

function Sidebar() {
  const sidebarStyle = {
    height: '100vh', // Full height
    width: '220px',
    position: 'fixed', // Fixed to the side
    left: 0,
    top: 0,
    paddingTop: '100px', // A little space below the header
    backgroundColor: '#f4f4f4',
    borderRight: '1px solid #ddd'
  };

  const linkStyle = {
    display: 'block',
    padding: '15px 20px',
    color: '#333',
    textDecoration: 'none',
    fontSize: '18px'
  };

  

  return (
    <nav style={sidebarStyle}>
      <a href="/" style={linkStyle}>Dashboard</a>
      <a href="/students" style={linkStyle}>Students</a>
      <a href="/reports" style={linkStyle}>Reports</a>
      <Link to="/invoices/new" style={linkStyle}>Issue Invoice</Link>
    </nav>
  );
  
}



export default Sidebar;
