// src/components/StudentTable.js
import React, { useState, useEffect } from 'react';
import axios from 'axios'; // Import axios

function StudentTable() {
  // 1. Create a state variable to store our students list
  const [students, setStudents] = useState([]);

  // 2. useEffect hook to fetch data when the component loads
  useEffect(() => {
    // Define the function to fetch data
    const fetchStudents = async () => {
      try {
        // Make a GET request to our backend API
        const response = await axios.get('http://127.0.0.1:8000/api/students/');
        // 3. Update the state with the data from the response
        setStudents(response.data);
      } catch (error) {
        console.error("There was an error fetching the students!", error);
      }
    };

    fetchStudents(); // Call the function
  }, []); // The empty array [] means this effect runs only once on mount

  return (
    <div>
      <h2>Students List</h2>
      <table style={{ width: '100%', borderCollapse: 'collapse' }}>
        <thead>
          <tr style={{ borderBottom: '2px solid black' }}>
            <th style={{ padding: '8px', textAlign: 'left' }}>Roll Number</th>
            <th style={{ padding: '8px', textAlign: 'left' }}>Full Name</th>
            <th style={{ padding: '8px', textAlign: 'left' }}>Course</th>
            <th style={{ padding: '8px', textAlign: 'left' }}>Semester</th>
          </tr>
        </thead>
        <tbody>
          {/* 4. Map over the students array to create a table row for each student */}
          {students.map(student => (
            <tr key={student.roll_number} style={{ borderBottom: '1px solid #ddd' }}>
              <td style={{ padding: '8px' }}>{student.roll_number}</td>
              <td style={{ padding: '8px' }}>{student.full_name}</td>
              <td style={{ padding: '8px' }}>{student.course}</td>
              <td style={{ padding: '8px' }}>{student.semester}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default StudentTable;
