import React, { useState, useEffect } from 'react';
import axios from 'axios';
import StudentTable from '../components/StudentTable';
import StudentForm from '../components/StudentForm';
import EditStudentModal from '../components/EditStudentModal';

function StudentsPage() {
  const [students, setStudents] = useState([]);
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [selectedStudent, setSelectedStudent] = useState(null);


  // Fetching logic (should be correct)
  useEffect(() => {
    const fetchStudents = async () => {
      try {
        const response = await axios.get('http://127.0.0.1:8000/api/students/');
        setStudents(response.data);
      } catch (error) {
        console.error("There was an error fetching the students!", error);
      }
    };
    fetchStudents();
  }, []);

  // Creation logic
  const handleStudentCreated = (newStudent) => {
    setStudents([...students, newStudent]);
  };

  // Deletion logic
  const handleDeleteStudent = async (rollNumber) => {
    if (window.confirm('Are you sure you want to delete this student?')) {
      try {
        await axios.delete(`http://127.0.0.1:8000/api/students/${rollNumber}/`);
        setStudents(students.filter(student => student.roll_number !== rollNumber));
        alert('Student deleted successfully!');
      } catch (error) {
        console.error("There was an error deleting the student!", error);
        alert('Error deleting student.');
      }
    }
  };

  const handleEditClick = (student) => {
    setSelectedStudent(student);
    setIsModalOpen(true);
  };

  const handleCloseModal = () => {
    setIsModalOpen(false);
    setSelectedStudent(null);
  };
  const handleStudentUpdated = (updatedStudent) => {
    // Find the student in the list and replace them with the updated data
    setStudents(students.map(s =>
      s.roll_number === updatedStudent.roll_number ? updatedStudent : s
    ));
  };

  return (
    <div>
      <h1>Student Management</h1>
      <p>Here you can view and manage student records.</p>
      <hr />
      <StudentForm onStudentCreated={handleStudentCreated} />

      {/* THE MOST IMPORTANT LINE: Ensure onDelete={handleDeleteStudent} exists here */}
      <StudentTable students={students} onDelete={handleDeleteStudent} />
      onEdit={handleEditClick}
    </div>
  );
  
}

export default StudentsPage;
